/********************************************************************************************************************
 * ENV 모듈
 * ******************************************************************************************************************/

import { Env } from './env.types';

const env: Env = {
  $$routerCount: 0,

  /********************************************************************************************************************
   * 개발 모드가 local 인지 여부
   * ******************************************************************************************************************/
  isLocal() {
    return process.env.APP_ENV === 'local';
  },
};

export default env;
